DROP TABLE IF EXISTS `sys_menu`;
CREATE TABLE `sys_menu` (
`id` varchar(19) NOT NULL COMMENT '菜单id(主键)',
`type` varchar(1) NOT NULL COMMENT '菜单类型：1目录，2菜单，3按钮',
`level` varchar(1) NOT NULL COMMENT '菜单级别：1一级菜单，2二级菜单，3三级菜单',
`parent_id` varchar(19) DEFAULT '' COMMENT '父级id',
`name` varchar(30) NOT NULL COMMENT '菜单名称',
`icon` varchar(100) DEFAULT '' COMMENT '图标',
`component` varchar(100) DEFAULT '' COMMENT '父组件',
`router_path` varchar(100) DEFAULT '' COMMENT '路由路径',
`router_name` varchar(100) DEFAULT '' COMMENT '路由名称',
`redirect` varchar(100) DEFAULT '' COMMENT '重定向',
`is_hide` varchar(1) NOT NULL DEFAULT '0' COMMENT '是否隐藏：0显示，1隐藏',
`rank` int(11) unsigned DEFAULT NULL COMMENT '排序',
`del_flag` varchar(1) NOT NULL DEFAULT '0' COMMENT '删除标记-平台：0否，1是',
`create_date` datetime NOT NULL COMMENT '创建时间',
`update_date` datetime NOT NULL COMMENT '更新时间',
PRIMARY KEY (`id`),
KEY `parent_id` (`parent_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='菜单信息表';

